import React from "react";

const Todo = ({todo, remove, complete}) => {

    getStyle = () => {
        textDecoration: complete ? 'line-through' : 'none'
    }
    return(
    <div style={this.getStyle()}>
        <p className="todos"> 
          {todo.text}
          <span className="removeBtn"
            onClick={()=>{remove(todo.id)}}
          >
            x
          </span>
          <span className="removeBtn"
            onClick={()=>{complete(todo.id)}}
          >
            Complete
          </span>
        </p>
    </div>
    )
}

export default Todo
