class TodoApp extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      data: []
    }
  }

  addTodo(value) {
    const newTodo = {
      text: "",
      id: window.id++
    }
    //updata data
    this.state.data.push(newTodo)
    //updata state
    this.setState({data: this.state.data})
  }

  removeTodo(id) {
    const reminder = this.state.data.filter(todo =>{
      if(todo.id !== id) {
        return todo
      }
    })
    this.setState({data: reminder})
  }

  render() {
    return(
      <div id="container">
        <Title />
        <TodoForm addTodo={this.addTodo.bind(this)} />
        <TodoList 
          todos={this.state.data} 
          remove={this.removeTodo.this.bind(this)} 
        />
        <Footer />
      </div>
    )
  }
}

export default App
